   function gui = createInterface(names) ... end;
   function updateInterface() ... end;
   function redrawDemo() ... end;

   % Callback subfunctions.
   function onMenuSelection() ... end;
   function onListSelection() ... end;
   function onDemoHelp() ... end;
   function onHelp() ... end;
   function onExit() ... end;

end  % Main function

